const TON = 1e9;

const makeStorageCollection = ({ nextItemIndex, softLimits, salesCount }) => {
    if (!softLimits) {
        softLimits = [ 100, 100, 100, 100, 100 ]
    }

    if (!salesCount) {
        salesCount = [ 0, 0, 0, 0, 0 ]
    }

    return [
        "Address", '0:' + OWNER_ADDRESS, // owner_address
        "uint64", nextItemIndex || "0",
        "cell", [ // content
            "cell", [
                'uint8', 1,
                'string', 'https://ton.org/collection.json'
            ],
            "cell", [
                'string', 'https://ton.org/meta/'
            ],   
        ],
        "boc", "", // nft_item_code
        "cell", [   // royalty
            'uint16', 0,
            'uint16', 0,
            'uint2', 0
        ],
        "cell", [
            'uint16', 3500,
            'uint16', 1000,
            'uint16', 375,
            'uint16', 100,
            'uint16', 25,

            'uint16', softLimits[0],
            'uint16', softLimits[1],
            'uint16', softLimits[2],
            'uint16', softLimits[3],
            'uint16', softLimits[4],

            'uint16', salesCount[0],
            'uint16', salesCount[1],
            'uint16', salesCount[2],
            'uint16', salesCount[3],
            'uint16', salesCount[4],
        ]
    ];
}



